def Clear(): os.system('cls')

def MainPrint(defaultPlayer):
    Clear()
    print('-'*24)
    print('.: FOOTBALL DATABASE :.')
    print('.:   PLAYER LOOKUP   :.')
    print('-'*24)
    print('{:^24s}'.format('Featured Player'))
    print(defaultPlayer)
    print('-'*24)
    print('Exit | Exit the program')

def CheckPlayer(userInput):
    if userInput.lower() == 'exit':
        os._exit(1)
    for player in players:
        if player['name'].lower()==userInput.lower():
            for team in teams:
                if team['id']==player['team_id']:
                    userInput=player['name']+'\n'+team['name']
                    return userInput
    return 'Player was not found in registry!'
    

defaultPlayer='Thomas Fredriksson'

while True:
    MainPrint(defaultPlayer)
    defaultPlayer=CheckPlayer(input('player > '))
    

